import React, {useContext} from 'react';
import DataContext from './DataContext';

interface LoadingProps {
  visible: boolean;
  message?: string;
}

export default function Loading({ visible, message }: LoadingProps) {
  if (!visible) return null;
  const image = "data:image/gif;base64,";
  
  
  const context = useContext(DataContext);
  
  const splitMessage = message?.split("<br/>") || ["Loading, please wait..."];
  
  const onClose = (event: React.MouseEvent<HTMLButtonElement>)=>{
    context?.setIsLoading?.(false);
  };
  
  const overlayStyle: React.CSSProperties = {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 50,
  };

  const contentStyle: React.CSSProperties = {
    textAlign: 'center',
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    position: 'relative', // To position the close button in the top right
  };

  const gifStyle: React.CSSProperties = {
    width: '50px',
    height: '50px',
    marginTop: '10px',
  };

  const closeButtonStyle: React.CSSProperties = {
    position: 'absolute',
    top: '0.3em',
    right: '0.3em',
    width: '1.5em',
    height: '1.5em',
    background: '#f1f1f1',
    borderRadius: '6px',
    border: '1px solid #ccc',
    padding: '0.1em',
    color: '#333', 
    fontSize: '1.1em', 
    cursor: 'pointer'
  };

  return (
    <div style={overlayStyle}>
      <div style={contentStyle}>
        <button style={closeButtonStyle} onClick={onClose}>
          Ã—
        </button>
        <div style={{ textAlign: 'center' }}>
          {splitMessage.map((line, index) => (
            <p key={index}>{line}</p>
          ))}
        </div>
        <img src={image} alt="Loading..." style={gifStyle} />
      </div>
    </div>
  );
}
